function CreateSeoUrl(fieldName) {
    fieldName = fieldName.toLowerCase(); // lowercase
    fieldName = fieldName.replace(/^\s+|\s+$/g, ''); // remove leading and trailing whitespaces
    fieldName = fieldName.replace(/\s+/g, '-'); // convert (continuous) whitespaces to one -
    fieldName = fieldName.replace(/[^a-z-0-9]/g, ''); // remove everything that is not [a-z] or -
    return fieldName;
}

$(document).ready(function () {
    $(function(){
        $('#tgs').bind('keypress', function(e) {
            if(e.keyCode == 13){
                var tag = ""
                var cval = $(this).val();
                tag+="<li class="+cval+">"+cval+"<a href='javascript:void(0)' onclick=removetg(\'"+cval+"\')>"
                tag+='<i class="fa fa-times"></i></a><input type="hidden" name="tags[]" value="'+cval+'"></li>'
                if (!$('#i_gz_x li').hasClass(cval))
                {
                    if ($.trim(cval) !="")
                    {
                        $('#i_gz_x').append(tag);
                        $(this).val('')
                    }
                }
                return false;
            }
        });

    });


    $('#b_name').blur(function() {
        if ($('#b_url').val() == '') {
            $('#b_url').val(CreateSeoUrl($(this).val()));
        }
    });
    $('#b_url').blur(function() {
        if ($(this).val() != '') {
            $('#b_url').val(CreateSeoUrl($(this).val()));
        }
    });

    $('.edturl').click(function () {
        $('#b_url').removeAttr('readonly');
    });
    $('#b_url').blur(function() {
        $(this).attr('readonly','readonly');
    });
})//ready here
